text:
.cnnix-golf__pullquote {
 position: relative;
 color: #262626;
 margin: 25px auto;
 padding: 10px 0 14px 0;
 width: 100%;
 max-width: 660px;
 border-left: 0;
 text-align: center;
}
.cnnix-golf__pullquote-quote:before, .cnnix-golf__pullquote-quote:after {
 position: absolute;
 content: '';
 width: 50px;
 height: 2px;
 left: 50%;
 transform: translateX(-50%);
 -webkit-transform: translateX(-50%);
 background-color: #262626;
}
.cnnix-golf__pullquote-quote:before {
 top: 0;
}
.cnnix-golf__pullquote-quote:after {
 bottom: -2px;
}
.cnnix-golf__pullquote-quote {
 position: relative;
 margin: 0;
 text-align: center;
 font-size: 35px;
 font-weight: 700;
 word-spacing: -1px;
 line-height: 1.15;
 padding: 24px 10px 22px 10px;
 margin-bottom: 24px;
}
.cnnix-golf__pullquote-cite {
 margin: 0;
 text-align: center;
 font-size: 12px;
 font-weight: 200;
 color: #262626;
 line-height: 1.15;
 padding: 0 10px;
 display: inline-block;
}
@media only screen and (min-width: 768px)  {
 .cnnix-golf__pullquote {
  margin: 50px auto;
 }
 .cnnix-golf__pullquote-quote {
  font-size: 35px;
 }
}

