.leftside-floating-image {
    width:100%;
    display:block;
    margin:0 auto 1.5rem;
}
@media screen and (min-width: 200px) {
    .leftside-floating-image {
        float:left;
        width:50%;
        max-width: 200px;
        display:block;
        margin:0 1.5rem 1rem 0;
    }
}
